<template>
  <div class="editor-cover-container">
    <h3>{{ title }}</h3>
    <div class="editor-cover-box">
      <div
        v-for="(n, index) in 3"
        :key="n"
        class="editor-cover"
        :class="fileList[index] && 'eidtor-cover-can-close'"
        @click="remove(fileList[index], index)"
      >
        <img v-if="fileList[index]" :src="fileList[index]" alt="">
      </div>
    </div>
  </div>
</template>

<script lang="ts">
import { defineComponent } from '@vue/composition-api'

export default defineComponent({
  props: {
    title: {
      type: String,
      default: ''
    },
    fileList: {
      type: Array,
      required: true
    }
  },
  emits: ['remove'],
  methods: {
    remove(url: string, i: number) {
      if (url) {
        this.$emit('remove', {
          url, i
        })
      }
    }
  }
})
</script>

